const CACHE_NAME="pit-calc-v1",ASSETS=["/","/index.html","/app.js","/style.css","/manifest.json"];self.addEventListener("install",e=>{e.waitUntil(caches.open(CACHE_NAME).then(e=>e.addAll(ASSETS))),self.skipWaiting()}),self.addEventListener("activate",e=>{e.waitUntil(caches.keys().then(e=>Promise.all(e.filter(e=>e!==CACHE_NAME).map(e=>caches.delete(e))))),self.clients.claim()}),self.addEventListener("fetch",e=>{"GET"===e.request.method&&e.request.url.startsWith(self.location.origin)&&e.respondWith(fetch(e.request).then(t=>{const s=t.clone();return caches.open(CACHE_NAME).then(t=>t.put(e.request,s)),t}).catch(()=>caches.match(e.request)))});